import { DatePicker } from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import {
  Button,
  // TextField,
  CircularProgress,
  Icon,
  Autocomplete,
  Box,
  styled,
  Card,
  Divider,
} from "@mui/material";
import { Span } from "app/components/Typography";
import { useEffect, useState } from "react";
import { TextValidator, ValidatorForm } from "react-material-ui-form-validator";
import fetch from 'cross-fetch';
import React from 'react';

const AutoComplete = styled(Autocomplete)(() => ({  }));

function sleep(delay = 0) {
  	return new Promise((resolve) => setTimeout(resolve, delay));
}
const TextField = styled(TextValidator)(() => ({
	width: "100%",
	marginBottom: "16px",
}));

const CardRoot = styled(Card)(({ theme }) => ({
	marginBottom: '24px',
	padding: '24px !important',
	[theme.breakpoints.down('sm')]: { paddingLeft: '16px !important' },
}));

const CardHeader = styled(Box)(() => ({
	display: 'flex',
	paddingLeft: '24px',
	paddingRight: '24px',
	marginBottom: '12px',
	alignItems: 'center',
	justifyContent: 'space-between',
}));

const Title = styled('span')(() => ({
	fontSize: '1rem',
	fontWeight: '500',
	textTransform: 'capitalize',
}));

const DriverAssignCard = ({ requisition }) => {
	const [state, setState] = useState({ date: new Date() });

	const handleSubmit = (event) => {
		// console.log("submitted");
		// console.log(event);
	};

	useEffect(() => {
		let active = true;

		if (!loading) {
		  return undefined;
		}

		(async () => {
		  const response = await axios.get('/hospital');
		  const response2 = await axios.get('/company');

		  const { hospitals } = response.data;
		  const { companies } = response2.data;

		  console.log(hospitals);

		  if (active) {
			console.log(hospitals);
			setHospitalOptions(hospitals);
			setCompaniesOptions(companies);
		  }

		})();

		return () => {
		  active = false;
		};
	  }, [loading, hospitalOptions]);

	  useEffect(() => {
		if (!open) {
		  setHospitalOptions([]);
		}
	  }, [open]);
	const handleChange = (event) => {
		event.persist();
		setState({ ...state, [event.target.name]: event.target.value });
	};

	const handleDateChange = (date) => setState({ ...state, date });

	const {
		username,
		firstName,
		creditCard,
		mobile,
		password,
		confirmPassword,
		gender,
		date,
		email,
	} = state;

	const [open, setOpen] = React.useState(false);
	const [options, setOptions] = React.useState([]);
	const loading = open && options.length === 0;

	React.useEffect(() => {
		let active = true;

		if (!loading) {
			return undefined;
		}

		(async () => {
			const response = await fetch('https://country.register.gov.uk/records.json?page-size=5000');
			await sleep(3000); // For demo purposes.
			const countries = await response.json();

			if (active) {
				setOptions(Object.keys(countries).map((key) => countries[key].item[0]));
			}
		})();

		return () => {
			active = false;
		};
	}, [loading]);

	React.useEffect(() => {
		if (!open) {
		setOptions([]);
		}
	}, [open]);

	return (
		<CardRoot>
		{/* <StyledCard elevation={0}></StyledCard> */}
			<ValidatorForm onSubmit={handleSubmit} onError={() => null}>

				<CardHeader>
					<Title>Assign Driver</Title>
				</CardHeader>

				<Divider sx={{ my: 3 }} />

				{/* <Grid container > */}
					<Box ml="-5px">
						<AutoComplete
							open={open}
							options={options}
							loading={loading}
							id="asynchronous-demo"
							onOpen={() => setOpen(true)}
							onClose={() => setOpen(false)}
							getOptionLabel={(option) => option.name}
							renderInput={(params) => (
							<TextField
								{...params}
								fullWidth
								variant="outlined"
								label="status"
								InputProps={{
								...params.InputProps,
								endAdornment: (
									<React.Fragment>
									{loading ? <CircularProgress color="inherit" size={20} /> : null}
									{params.InputProps.endAdornment}
									</React.Fragment>
								),
								}}
							/>
							)}
						/>
					</Box>
					<Box ml="-5px">
						<AutoComplete
						open={open}
						options={options}
						loading={loading}
						id="asynchronous-demo"
						onOpen={() => setOpen(true)}
						onClose={() => setOpen(false)}
						getOptionLabel={(option) => option.name}
						renderInput={(params) => (
							<TextField
							{...params}
							// fullWidth
							variant="outlined"
							label="Driver"
							InputProps={{
								...params.InputProps,
								endAdornment: (
								<React.Fragment>
									{loading ? <CircularProgress color="inherit" size={20} /> : null}
									{params.InputProps.endAdornment}
								</React.Fragment>
								),
							}}
							/>
						)}
						/>
					</Box>
				{/* </Grid> */}

				<Button color="primary" variant="contained" type="submit">
					<Icon>send</Icon>
					<Span sx={{ pl: 1, textTransform: "capitalize" }}>Submit</Span>
				</Button>
			</ValidatorForm>
		</CardRoot>
	);
};

export default DriverAssignCard;
