import { TimePicker} from "@mui/lab";
import AdapterDateFns from "@mui/lab/AdapterDateFns";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Grid,
  Icon,
  Autocomplete,
  Box,
  styled,
  Card,
  Divider,
} from "@mui/material";
import { Span, H1, H2, H3, H5, Small } from "app/components/Typography";
import { useEffect, useState } from "react";
import { TextValidator, ValidatorForm } from "react-material-ui-form-validator";
import fetch from 'cross-fetch';
import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import SimpleCard from 'app/components/SimpleCard';

const AutoComplete = styled(Autocomplete)(() => ({  }));

function sleep(delay = 0) {
  	return new Promise((resolve) => setTimeout(resolve, delay));
}
const TextField = styled(TextValidator)(() => ({
	width: "100%",
	marginBottom: "16px",
}));

const CardRoot = styled(Card)(({ theme }) => ({
	marginBottom: '24px',
	padding: '24px !important',
	[theme.breakpoints.down('sm')]: { paddingLeft: '16px !important' },
}));

const CardHeader = styled(Box)(() => ({
	display: 'flex',
	paddingLeft: '24px',
	paddingRight: '24px',
	marginBottom: '12px',
	alignItems: 'center',
	justifyContent: 'space-between',
}));

const Title = styled('span')(() => ({
	fontSize: '1rem',
	fontWeight: '500',
	textTransform: 'capitalize',
}));

const ProductTable = styled(Table)(() => ({
    minWidth: 400,
    whiteSpace: 'pre',
    '& small': {
      width: 50,
      height: 15,
      borderRadius: 500,
      boxShadow: '0 0 2px 0 rgba(0, 0, 0, 0.12), 0 2px 2px 0 rgba(0, 0, 0, 0.24)',
    },
    '& td': { borderBottom: 'none' },
    '& td:first-of-type': { paddingLeft: '16px !important' },
  }));

const EditManifest = () => {
	const [state, setState] = useState({ date: new Date() });
    const navigate = useNavigate();
    const manifest = useLocation();
    const [selectedDate, setSelectedDate] = React.useState(
        new Date('2014-08-18T21:11:54')
    )
    const onClick = () => navigate(-1);

    console.log("EditManifest manifest: " + manifest.state);
	useEffect(() => {
		ValidatorForm.addValidationRule("isPasswordMatch", (value) => {
		if (value !== state.password) return false;

		return true;
		});
		return () => ValidatorForm.removeValidationRule("isPasswordMatch");
	}, [state.password]);

	const handleSubmit = (event) => {
		// console.log("submitted");
		// console.log(event);
	};

	const handleChange = (event) => {
		event.persist();
		setState({ ...state, [event.target.name]: event.target.value });
	};

	// const handleDateChange = (date) => setState({ ...state, date });

    function handleDateChange(date) {
        setSelectedDate(date)
    }
	const {
		status,
		operator_in,
		time_in,
		operator_out,
		time_out,
		landfill,
		notes,
		wasteType,
		treatment,
        bin_location,
        bin_qtybin_qty,
        bin_size,
        waste_mass,
        bin_qty
	} = state;

	const [open, setOpen] = React.useState(false);


	return (
        <CardRoot>
            <SimpleCard elevation={0}>
                <CardRoot>
                    <ValidatorForm onSubmit={handleSubmit} onError={() => null}>

                        <Box ml="-5px" style={{textAlign: 'right'}}>
                            <Button color="primary" variant="contained" onClick={onClick}>
                                <Span sx={{ pl: 1, textTransform: "capitalize" }}>Back</Span>
                            </Button>
                        </Box>

                        <Divider sx={{ my: 5 }} />

                        <CardHeader>
                            <H1>Manifest    <Title>Edit</Title></H1>
                        </CardHeader>

                        <Divider sx={{ my: 5 }} />

                        <Grid container spacing={3}>
                            <Grid item lg={6} md={6} sm={12} xs={12}>
                                <H2>{manifest.company_name}</H2>
                                <H5>{manifest.requisition_id}</H5>
                            </Grid>
                            <Grid item lg={6} md={6} sm={12} xs={12} style={{alignSelf: 'flex-end'}} >
                                <Box ml="-5px" style={{textAlign: 'right'}}>

                                    <AutoComplete
                                        value={status}
                                        options={suggestions}
                                        getOptionLabel={(option) => option.label}
                                        renderInput={(params) => (
                                        <TextField {...params} label="Manifest Status" variant="outlined"  />
                                        )}
                                    />
                                    <H5>Manifest Date:  <Small style={{ marginLeft: '2.5rem' }}>{manifest.created_date}</Small></H5>
                                </Box>
                            </Grid>
                        </Grid>

                        <Divider sx={{ my: 5 }} />

                        <Grid container spacing={3}>
                            <Grid item lg={6} md={6} sm={12} xs={12}>
                                <H3>Collection </H3>
                                <TextField
                                    type="text"
                                    name="operator_in"
                                    label={"Operator In"}
                                    onChange={handleChange}
                                    value={operator_in || ""}
                                    validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                    errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                />
                                <LocalizationProvider dateAdapter={AdapterDateFns}>
                                    <Grid
                                        container
                                        sx={{ width: '60%' }}
                                        justify="space-around"
                                    >
                                        <TimePicker
                                            value={selectedDate}
                                            onChange={handleDateChange}
                                            renderInput={(props) => (
                                                <TextField
                                                    {...props}
                                                    variant="standard"
                                                    id="time_in"
                                                    label="Time In"
                                                />
                                            )}
                                        />
                                    </Grid>
                                </LocalizationProvider>
                                <TextField
                                    type="text"
                                    name="operator_out"
                                    label={"Operator Out"}
                                    onChange={handleChange}
                                    value={operator_out || ""}
                                    validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                    errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                />
                                <LocalizationProvider dateAdapter={AdapterDateFns}>
                                    <Grid
                                        container
                                        sx={{ width: '60%' }}
                                        justify="space-around"
                                    >
                                        <TimePicker
                                            value={selectedDate}
                                            onChange={handleDateChange}
                                            renderInput={(props) => (
                                                <TextField
                                                    {...props}
                                                    variant="standard"
                                                    id="time_out"
                                                    label="Time Out"
                                                />
                                            )}
                                        />
                                    </Grid>
                                </LocalizationProvider>
                            </Grid>
                            <Grid item lg={6} md={6} sm={12} xs={12} style={{alignSelf: 'flex-end'}} >
                                <Box ml="-5px" style={{textAlign: 'right'}}>
                                    <H3>Disposal</H3>

                                    <TextField
                                        type="text"
                                        name="landfill"
                                        label={"Landfill"}
                                        onChange={handleChange}
                                        value={landfill || ""}
                                        validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                        errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                    />
                                    <TextField
                                        type="text"
                                        name="notes"
                                        label={"Notes"}
                                        onChange={handleChange}
                                        value={notes || ""}
                                        validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                        errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                    />
                                </Box>
                            </Grid>
                        </Grid>

                        <Divider sx={{ my: 5 }} />

                        <Card elevation={3} sx={{ pt: '20px', mb: 3 }}>
                            <Box overflow="auto">
                                <ProductTable>
                                <TableHead>
                                    <TableRow>
                                    <TableCell sx={{ px: 0 }} colSpan={2}>
                                        Waste Type
                                    </TableCell>
                                    <TableCell sx={{ px: 0 }} colSpan={2}>
                                        Treatment
                                    </TableCell>
                                    <TableCell sx={{ px: 0 }} colSpan={2}>
                                        Bin Location
                                    </TableCell>
                                    <TableCell sx={{ px: 0 }} colSpan={2}>
                                        Bin Qty
                                    </TableCell>
                                    <TableCell sx={{ px: 0 }} colSpan={2}>
                                        Bin Sizes
                                    </TableCell>
                                    <TableCell sx={{ px: 0 }} colSpan={2}>
                                        Mass (Kg)
                                    </TableCell>
                                    </TableRow>
                                </TableHead>

                                <TableBody>
                                    {/* {productList.map((product, index) => ( */}
                                    <TableRow  hover>

                                        <TableCell colSpan={2} align="left" sx={{ px: 0, textTransform: 'capitalize' }}>
                                        <Box display="flex" alignItems="center">
                                            {/* <Paragraph sx={{ m: 0, ml: 0 }}>{manifest.wasteType}</Paragraph> */}
                                            <TextField
                                                type="text"
                                                name="wasteType"
                                                label={"WasteType"}
                                                onChange={handleChange}
                                                value={wasteType || ""}
                                                validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                                errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                            />
                                        </Box>
                                        </TableCell>

                                        <TableCell colSpan={2} align="left" sx={{ px: 0, textTransform: 'capitalize' }}>
                                        <Box display="flex" alignItems="center">
                                            {/* <Paragraph sx={{ m: 0, ml: 0 }}>{manifest.treatment}</Paragraph> */}
                                            <TextField
                                                type="text"
                                                name="treatment"
                                                label={"Treatment"}
                                                onChange={handleChange}
                                                value={treatment || ""}
                                                validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                                errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                            />
                                        </Box>
                                        </TableCell>

                                        <TableCell colSpan={2} align="left" sx={{ px: 0, textTransform: 'capitalize' }}>
                                        <Box display="flex" alignItems="center">
                                            {/* <Paragraph sx={{ m: 0, ml: 0 }}>{manifest.bin_location}</Paragraph> */}
                                            <TextField
                                                type="text"
                                                name="bin_location"
                                                label={"Bin Location"}
                                                onChange={handleChange}
                                                value={bin_location || ""}
                                                validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                                errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                            />
                                        </Box>
                                        </TableCell>

                                        <TableCell colSpan={2} align="left" sx={{ px: 0, textTransform: 'capitalize' }}>
                                        <Box display="flex" alignItems="center">
                                            {/* <Paragraph sx={{ m: 0, ml: 0 }}>{manifest.bin_qty}</Paragraph> */}
                                            <TextField
                                                type="text"
                                                name="bin_qty"
                                                label={"Bin Qty"}
                                                onChange={handleChange}
                                                value={bin_qty || ""}
                                                validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                                errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                            />
                                        </Box>
                                        </TableCell>

                                        <TableCell colSpan={2} align="left" sx={{ px: 0, textTransform: 'capitalize' }}>
                                        <Box display="flex" alignItems="center">
                                            {/* <Paragraph sx={{ m: 0, ml: 0 }}>{manifest.bin_size}</Paragraph> */}
                                            <TextField
                                                type="text"
                                                name="bin_size"
                                                label={"Bin Size"}
                                                onChange={handleChange}
                                                value={bin_size || ""}
                                                validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                                errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                            />
                                        </Box>
                                        </TableCell>

                                        <TableCell colSpan={2} align="left" sx={{ px: 0, textTransform: 'capitalize' }}>
                                        <Box display="flex" alignItems="center">
                                            {/* <Paragraph sx={{ m: 0, ml: 0 }}>{manifest.waste_mass}</Paragraph> */}

                                            <TextField
                                                type="text"
                                                name="waste_mass"
                                                label={"Waste Mass"}
                                                onChange={handleChange}
                                                value={waste_mass || ""}
                                                validators={["required",  "minStringLength:1", "maxStringLength: 100"]}
                                                errorMessages={["this field is required", "this field requires 100 characters", "this field requires 100 characters"]}
                                            />
                                        </Box>
                                        </TableCell>

                                        {/* <TableCell align="left" colSpan={2} sx={{ px: 0, textTransform: 'capitalize' }}>
                                        ${product.price > 999 ? (product.price / 1000).toFixed(1) + 'k' : product.price}
                                        </TableCell>

                                        <TableCell sx={{ px: 0 }} align="left" colSpan={2}>
                                        {product.available ? (
                                            product.available < 20 ? (
                                            <Small bgcolor={bgSecondary}>{product.available} available</Small>
                                            ) : (
                                            <Small bgcolor={bgPrimary}>in stock</Small>
                                            )
                                        ) : (
                                            <Small bgcolor={bgError}>out of stock</Small>
                                        )}
                                        </TableCell> */}

                                    </TableRow>
                                    {/*  ))} */}
                                </TableBody>
                                </ProductTable>
                            </Box>
                        </Card>


                        <Divider sx={{ my: 5 }} />

                        <Box ml="-5px" style={{textAlign: 'right'}}>
                            <H5>Total Mass (Kg):  <Small style={{ marginLeft: '2.5rem' }}>{manifest.waste_mass}</Small></H5>
                            <H5>Tonnage Charged:  <Small style={{ marginLeft: '2.5rem' }}>{manifest.waste_mass}</Small></H5>
                        </Box>

                        <Divider sx={{ my: 5 }} />

                        <Box ml="-5px" style={{textAlign: 'right'}}>
                            <Button color="primary" variant="contained" type="submit">
                                <Icon>send</Icon>
                                <Span sx={{ pl: 1, textTransform: "capitalize" }}>Submit</Span>
                            </Button>
                        </Box>
                    </ValidatorForm>
                </CardRoot>
            </SimpleCard>
        </CardRoot>
	);
};
const suggestions = [
    { label: 'Afghanistan' },
    { label: 'Aland Islands' },
    { label: 'Albania' },
    { label: 'Algeria' },
    { label: 'American Samoa' },
  ];
export default EditManifest;
