import { DatePicker } from '@mui/lab';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import {
  Button,
  // TextField,
  CircularProgress,
  Icon,
  Autocomplete,
  Box,
  styled,
  Card,
  Divider,
} from '@mui/material';
import { Span } from 'app/components/Typography';
import { useEffect, useState, Fragment } from 'react';
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator';
import axios from 'app/api/axios';

const AutoComplete = styled(Autocomplete)(() => ({}));

function sleep(delay = 0) {
  return new Promise((resolve) => setTimeout(resolve, delay));
}
const TextField = styled(TextValidator)(() => ({
  width: '100%',
  marginBottom: '16px',
}));

const CardRoot = styled(Card)(({ theme }) => ({
  marginBottom: '24px',
  padding: '24px !important',
  [theme.breakpoints.down('sm')]: { paddingLeft: '16px !important' },
}));

const CardHeader = styled(Box)(() => ({
  display: 'flex',
  paddingLeft: '24px',
  paddingRight: '24px',
  marginBottom: '12px',
  alignItems: 'center',
  justifyContent: 'space-between',
}));

const Title = styled('span')(() => ({
  fontSize: '1rem',
  fontWeight: '500',
  textTransform: 'capitalize',
}));
const accessToken = window.localStorage.getItem('accessToken');

const DriverAssignCard = ({ requisition }) => {
//   const [state, setState] = useState({ date: new Date() });
  const [open, setOpen] = useState(false);
  const [options, setOptions] = useState([]);
  const loading = open && options.length === 0;
  const [hospitalOptions, setHospitalOptions] = useState([]);
  const [hospitalDropdownValue, setHospitalDropdownValue] = useState(null);
  const [manifeststatus, setManifeststatus] = useState(null);

  useEffect(() => {
    let active = true;
    let userlist = [];

    if (!loading) {
      return undefined;
    }

    (async () => {
      const response = await axios.get('/users');

      const { users } = response.data;

      //   console.log(users);
      users.forEach((user) => {
        console.log('user.role: ' + user.role);
        if (user.role === 'driver') {
			console.log('user: ' + user.name);
			userlist.push({
				id: user._id,
				name: user.name +' '+ user.surname
			})
        }
      });
	  setHospitalOptions(userlist);
	  console.log('hospitalOptions: ' + hospitalOptions);

      //   if (active) {
    //   console.log('hospitalOptions: ' + hospitalOptions);
      // 	setHospitalOptions(users);
      //   }
    })();

    return () => {
      active = false;
    };
  }, [loading, hospitalOptions]);

  useEffect(() => {
    if (!open) {
      setHospitalOptions([]);
    }
  }, [open]);

  const handleHospitalDropdownChange = (_, newValue) => {
    console.log('newValue');
    console.log(newValue);
    setHospitalDropdownValue(newValue);
  };

  const handleManifeststatus = (_, newValue) => {
    if (newValue && newValue.inputValue) {
      setManifeststatus({ label: newValue.inputValue });
      return;
    }
    setManifeststatus(newValue);
  };

  const handleSubmit = async () => {
    try {

		var manifest_url = '/manifest/' + manifest.state.manifest._id;
		var requisition_url = '/requisition/' + requisition._id;
    	// console.log('accessToken: ' + accessToken);
    	// const response = await axios.patch('url', {
    	//   name: event.target.username.value,
    	//   surname: event.target.surname.value,
    	//   role: event.target.role.value,
    	//   password: event.target.password.value,
    	//   company: companyname,
    	//   company_id: company_Id,
    	//   is_active: event.target.userstatus.value,
    	//   phone: event.target.mobile.value,
    	//   email: event.target.email.value,
    	//   created_date: new Date(),
    	//   updated_at: new Date(),
    	//   headers: {
    	// 	'Content-Type': 'application/json',
    	// 	Authorization: `Bearer ${accessToken}`,
    	//   },
    	//   // withCredentials: true
    	// });
    	// const { status } = response.data;
    	// if (status === 'success') {
    	//   // setOpen(true);
    	// //   navigate('/netcare/admin/user');
    	// }
    } catch (error) {
    	console.log('error: ' + error);
    }
  };

  return (
    <CardRoot>
      {/* <StyledCard elevation={0}></StyledCard> */}
      <ValidatorForm onSubmit={handleSubmit} onError={() => null}>
        <CardHeader>
          <Title>Assign Driver</Title>
        </CardHeader>

        <Divider sx={{ my: 3 }} />

        {/* <Grid container > */}
        <Box ml="-5px">
          <AutoComplete
            value={manifeststatus}
            options={manifest_status}
            onChange={handleManifeststatus}
            getOptionLabel={(option) => option.label}
            renderInput={(params) => (
              <TextField {...params} label="Manifest Status" variant="outlined" />
            )}
          />
        </Box>
        <Box ml="-5px">
          <AutoComplete
            open={open}
            options={hospitalOptions}
            loading={loading}
            onChange={handleHospitalDropdownChange}
            id="driver"
            onOpen={() => setOpen(true)}
            onClose={() => setOpen(false)}
            getOptionLabel={(option) => option.name }
			isOptionEqualToValue={(option, value) => option.name === value.name}
            renderInput={(params) => (
              <TextField
                {...params}
                fullWidth
                variant="outlined"
                label="Driver"
                InputProps={{
                  ...params.InputProps,
                  endAdornment: (
                    <Fragment>
                      {loading ? <CircularProgress color="inherit" size={20} /> : null}
                      {params.InputProps.endAdornment}
                    </Fragment>
                  ),
                }}
              />
            )}
          />
        </Box>
        {/* </Grid> */}

        <Button color="primary" variant="contained" type="submit">
          <Icon>send</Icon>
          <Span sx={{ pl: 1, textTransform: 'capitalize' }}>Submit</Span>
        </Button>
      </ValidatorForm>
    </CardRoot>
  );
};
const manifest_status = [
  { label: 'Awaiting Acceptance' },
  { label: 'Assigned to Driver' },
  { label: 'Waste Collected' },
  { label: 'En Route to Sorting' },
  { label: 'En Route to Landfill' },
  { label: 'En Route to Scrapyard/Recycling Deport' },
  { label: 'Sorting Facility' },
  { label: 'Handed Over' },
  { label: 'Waste Disposed' },
];

export default DriverAssignCard;
