import { Box, Button, Card, styled, Grid, Divider } from '@mui/material';
import { convertHexToRGB } from 'app/utils/utils';
import { Small, H2, H3, H4 } from 'app/components/Typography';
import axios from 'app/api/axios';
import useAuth from 'app/hooks/useAuth';
import { useNavigate } from 'react-router-dom';

const CardRoot = styled(Card)(({ theme }) => ({
  marginBottom: '24px',
  padding: '24px !important',
  [theme.breakpoints.down('sm')]: { paddingLeft: '16px !important' },
}));

const StyledCard = styled(Card)(({ theme }) => ({
  boxShadow: 'none',
  textAlign: 'center',
  position: 'relative',
  padding: '24px !important',
  background: `rgb(${convertHexToRGB(theme.palette.primary.main)}, 0.15) !important`,
  [theme.breakpoints.down('sm')]: { padding: '16px !important' },
}));

const StyledCard2 = styled(Card)(({ theme }) => ({
	boxShadow: 'none',
	textAlign: 'left',
	position: 'relative',
	padding: '24px !important',
	background: `rgb(${convertHexToRGB(theme.palette.primary.main)}, 0.15) !important`,
	[theme.breakpoints.down('sm')]: { padding: '16px !important' },
}))

const accessToken = window.localStorage.getItem('accessToken');

const DetailsCard = ({ requisition }) => {
	let { user } = useAuth();
    const navigate = useNavigate();

	const onClickGenerateManifest = async () => {
		try {
			console.log('requisition.requisition_id: ' + requisition.requisition_id);
			var url = '/manifest';
			// const response = await axios.post( url, {
			// 	company_name: requisition.serviceProviderName,
			// 	wasteType: '',
			// 	wasteCategory: '',
			// 	incident: false,
			// 	incident_type: '',
