			// 	operator_out: '',
			// 	operator_name: '',
			// 	landfill: '',
			// 	notes: '',
			// 	time_in: '',
			// 	time_out: '',
			// 	treatment: '',
			// 	bin_location: '',
			// 	bin_qty: '',
			// 	bin_size: '',
			// 	waste_mass: '',
			// 	wasteCollected: false,
			// 	wasteDiverted: false,
			// 	wasteDisposed: false,
			// 	updated_at: new Date(),
			// 	headers: {
			// 		'Content-Type': 'application/json',
			// 		Authorization: `Bearer ${accessToken}`,
			// 	},
			// });
			// const { status, manifests } = response.data;
			// if (status === 'success') {
			// 	// setOpen(true);
			// 	//   navigate('/netcare/admin/user');

			// 	var requisition_url = '/requisition/' + requisition._id;
			// 	const response = await axios.patch( requisition_url, {
			// 		createManifest: true,
			// 		updated_at: new Date(),
			// 		headers: {
			// 			'Content-Type': 'application/json',
			// 			Authorization: `Bearer ${accessToken}`,
			// 		},
			// 	});
			// 	const { status, requisitions } = response.data;
			// 	if (status === 'success') {
			// 		navigate(-1);
			// 	}
			// }
		} catch (error) {
			console.log('patch_error2: ' + error);
		}


	}

	const GenerateManifestButton = (requisition) => {
		if (!requisition.createManifest) {
			return (
				<>
					<Grid item lg={6} md={6} sm={12} xs={12}>
						<Button
							size="large"
							color="primary"
							variant="contained"
							sx={{ textTransform: 'uppercase' }}
							onClick={onClickGenerateManifest}
						>
							Generate Manifest
						</Button>
					</Grid>
					<Grid item lg={6} md={6} sm={12} xs={12}>
					</Grid>
				</>
			);
		}
	}
	return (
		<CardRoot>
			<StyledCard elevation={0}>
				{/* <img src="/assets/images/illustrations/upgrade.svg" alt="upgrade" /> */}
				<H3>ID: {requisition.requision_id}</H3>
				<Divider sx={{ my: 3 }} />
				<Grid container spacing={3} >

					<Grid item lg={6} md={3} sm={12} xs={12}>
					<StyledCard2>
						<Box ml="-5px">
							<H4>To: <small>{requisition.serviceProviderName}</small></H4>
							<H4>WasteType: <small>{requisition.wasteType}</small></H4>
							<H4>Requestor: <small>{requisition.hospitalName}</small></H4>
							<H4>Creation Date: <small>{requisition.created_date}</small></H4>
							{/* <Heading>Status: item.amount</Heading> */}
						</Box>	
					</StyledCard2>
					</Grid>
					<Grid item lg={6} md={6} sm={12} xs={12}>
					<StyledCard2>
						<Box ml="-5px">
							<H4>Collection Address: <small>{requisition.collection_address}</small></H4>
							<H4>Bin Location: <small>{requisition.bin_location}</small></H4>
							<H4>Expected qty: <small>{requisition.expected_qty}</small></H4>
							<H4>Colletion Date: <small>{requisition.collection_Date}</small></H4>
							{/* <Heading>Status: item.amount</Heading> */}
						</Box>
					</StyledCard2>

					</Grid>
					<Divider sx={{ my: 3 }} />
					{GenerateManifestButton(requisition)}

				</Grid>
			</StyledCard>
		</CardRoot>
	);
};

export default DetailsCard;
